//Use Freezed to define a Slides
//Use riverpod for reading the json file

import 'package:freezed_annotation/freezed_annotation.dart';
// ignore: unused_import
import 'package:flutter/foundation.dart';
import 'package:flutter_gyw/flutter_gyw.dart';

// required: associates our `drawing_portfolio.dart` with the code generated by Freezed
part 'slides.freezed.dart';
// optional: Since our Person class is serializable, we must add this line.
// But if Person was not serializable, we could skip it.
part 'slides.g.dart';

@freezed
class Slides with _$Slides {
  const factory Slides({
    required String name,
    required List<GYWDrawing> drawings,
  }) = _Slides;

  factory Slides.fromJson(Map<String, Object?> json)
      => _$SlidesFromJson(json);
}